import Box from "@material-ui/core/Box";
// import Grid from "@material-ui/core/Grid";
// import LockOutlinedIcon from "@material-ui/icons/LockOutlined";
// import Typography from "@material-ui/core/Typography";
import { makeStyles } from "@material-ui/core/styles";
import { storage, firestore } from "../../firebase/firebase.utils";
import ImageUpload from "./imageupload.component";
// 
const handleChange=(event)=> {

  const file = Array.from(event.target.files);
  this.setState({ file });   
}
const fileuploadHandler = () => {

  const storageRef = storage().ref();
this.state.file.forEach((file) => {
  storageRef
      .child(`images/${file.name}`)
      .putFile(file).then((snapshot) => {
  })
});
}

export default function PaymentForm() {
  const [productname, setProductname] = useState("");
  const [expirydate, setExpiryDate] = useState("");
  const [description,setDescription]=useState("");
  var status2 = [productname,expirydate,description];
  return (
    <React.Fragment>
      <Typography variant="h6" gutterBottom>
        Product Details
      </Typography>
      <Grid container spacing={3}>
        {/* <Grid item xs={12} md={6}>
          <TextField
            required
            id="cardName"
            label="Name on card"
            fullWidth
            autoComplete="cc-name"
          />
        </Grid> */}
        <Grid item xs={12} md={6}>
          <TextField
            required
            id="product-name"
            label="Product Name"
            fullWidth
            // autoComplete="cc-number"
            onChange={(e) => {
              setProductname(e.target.value);
              // localStorage.setItem("First",firstname);
            }}
          />
        </Grid>
        <Grid item xs={12} md={6}>

        </Grid>
        <Grid item xs={12} md={6}>
        <Typography variant="h6" gutterBottom>
          Description
      </Typography>
        <textarea
                      className="b pa2 input-reset ba bg-transparent hover-bg-black hover-white "
                      id="description"
                      name="description"
                      rows="5"
                      cols="55"
                      label="Description"
                      onChange={(e) => {
                        setDescription(e.target.value);
                        localStorage.setItem("status2", JSON.stringify(status2));
                        // localStorage.setItem("First",firstname);
                      }}
                      // onChange={(e) => {
                      //   setSymptoms(e.target.value);
                      // }}
                    ></textarea>
        </Grid>
        <Grid item xs={12}>
        <label className="fileUploaderContainer">
        <Typography variant="h6" gutterBottom>
        Click here to upload Images<br/>
      </Typography>

            {/* <input
              type="file"
              id="file"
              onChange={(e)=>{handleChange.bind()}}
              required multiple
            />
             <button onClick={(e)=>{fileuploadHandler()}}>Upload!</button>    */}
             <ImageUpload />
          </label>
        </Grid>


      </Grid>
    </React.Fragment>
  );
}
